import React, { useState, useEffect, useRef } from 'react';

function App() {
  const [mode, setMode] = useState('countdown'); // 'countdown' or 'record'
  // Countdown mode states
  const [minutes, setMinutes] = useState(1);
  const [seconds, setSeconds] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [inputMinutes, setInputMinutes] = useState(1);
  // Record mode states
  const [isRecording, setIsRecording] = useState(false);
  const [mediaStream, setMediaStream] = useState(null);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [recordedChunks, setRecordedChunks] = useState([]);
  const [videoURL, setVideoURL] = useState(null);
  const videoRef = useRef(null);

  // Countdown timer effect
  useEffect(() => {
    if (mode !== 'countdown') return;
    let timer = null;
    if (isRunning && (minutes > 0 || seconds > 0)) {
      timer = setInterval(() => {
        if (seconds === 0) {
          if (minutes === 0) {
            setIsRunning(false);
          } else {
            setMinutes(m => m - 1);
            setSeconds(59);
          }
        } else {
          setSeconds(s => s - 1);
        }
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [isRunning, minutes, seconds, mode]);

  // Record mode: handle video recording
  useEffect(() => {
    if (mode !== 'record') return;
    if (isRecording) {
      navigator.mediaDevices.getUserMedia({ video: true, audio: true })
        .then(stream => {
          setMediaStream(stream);
          if (videoRef.current) {
            videoRef.current.srcObject = stream;
          }
          const recorder = new window.MediaRecorder(stream);
          setMediaRecorder(recorder);
          setRecordedChunks([]);
          recorder.ondataavailable = (e) => {
            if (e.data.size > 0) {
              setRecordedChunks(prev => [...prev, e.data]);
            }
          };
          recorder.onstop = () => {
            const blob = new Blob(recordedChunks, { type: 'video/webm' });
            setVideoURL(URL.createObjectURL(blob));
            if (stream) {
              stream.getTracks().forEach(track => track.stop());
            }
          };
          recorder.start();
        })
        .catch(err => {
          alert('Could not access camera/microphone.');
          setIsRecording(false);
        });
    } else {
      if (mediaRecorder && mediaRecorder.state !== 'inactive') {
        mediaRecorder.stop();
      }
      if (mediaStream) {
        mediaStream.getTracks().forEach(track => track.stop());
      }
    }
    // Cleanup
    return () => {
      if (mediaStream) {
        mediaStream.getTracks().forEach(track => track.stop());
      }
    };
    // eslint-disable-next-line
  }, [isRecording, mode]);

  const startTimer = () => {
    setMinutes(inputMinutes);
    setSeconds(0);
    setIsRunning(true);
  };

  const resetTimer = () => {
    setIsRunning(false);
    setMinutes(inputMinutes);
    setSeconds(0);
  };

  const isFlashRed = isRunning && minutes === 0 && seconds <= 10 && (minutes > 0 || seconds > 0);

  // Record mode handlers
  const startRecording = () => {
    setVideoURL(null);
    setIsRecording(true);
  };
  const stopRecording = () => {
    setIsRecording(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Counter App</h1>
        <div style={{ marginBottom: '20px' }}>
          <button onClick={() => setMode('countdown')} disabled={mode === 'countdown'}>Countdown Mode</button>
          <button onClick={() => setMode('record')} disabled={mode === 'record'}>Record Mode</button>
        </div>
        {mode === 'countdown' && (
          <div>
            <label>
              Set Minutes:
              <input
                type="number"
                min="1"
                value={inputMinutes}
                onChange={e => setInputMinutes(Math.max(1, Number(e.target.value)))}
                disabled={isRunning}
              />
            </label>
            <button onClick={startTimer} disabled={isRunning}>Start</button>
            <button onClick={resetTimer}>Reset</button>
            <div
              style={{
                fontSize: '3rem',
                margin: '20px',
                color: isFlashRed ? 'red' : 'black',
                transition: 'color 0.2s',
                animation: isFlashRed ? 'flash 0.5s alternate infinite' : 'none'
              }}
            >
              {`${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`}
            </div>
            <style>
              {`
                @keyframes flash {
                  from { color: red; }
                  to { color: black; }
                }
              `}
            </style>
          </div>
        )}
        {mode === 'record' && (
          <div>
            <h2>Record Mode</h2>
            <div>
              {!isRecording && (
                <button onClick={startRecording}>Start Recording</button>
              )}
              {isRecording && (
                <button onClick={stopRecording}>Stop Recording</button>
              )}
            </div>
            <div style={{ margin: '20px 0' }}>
              <video
                ref={videoRef}
                autoPlay
                muted={isRecording}
                controls={!!videoURL}
                style={{ width: '400px', height: '300px', background: '#222' }}
                src={videoURL || undefined}
              />
            </div>
            {videoURL && (
              <div>
                <a href={videoURL} download="recording.webm">Download Recording</a>
              </div>
            )}
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
